mapper.select:
SELECT mb_id mbId, mb_name mbName, mb_phone mbPhone,mb_birth mbBirth,mb_mail mbMail, mb_pw mbPw, mb_nick_name mbNickname,mb_admin mbAdmin,mb_gender mbGender, mb_flag mbFlag
from mb
WHERE mb_id = #{mbId}

mapper.update:
UPDATE mb
SET mb_id = #{mbId}, mb_name = #{mbName}, mb_phone = #{mbPhone}, mb_birth = #{mbBirth}, mb_pw = #{mbPw} , mb_nick_name = #{mbNickname}, mb_admin = #{mbAdmin}, mb_gender = #{mbGender}, mb_flag = #{mbFlag}
WHERE mb_id=#{mbId}
