mapper.select:
SELECT pst_id pstId, pst_title pstTitle, pst_text pstText
FROM pst
WHERE pst_id = #{pstId}

mapper.update:
UPDATE pst SET
	pst_title = #{pstTitle}
	pst_text = #{pstText}
	pst_create_date = #{pstCreateDate}
	pst_update_date = #{pstUpdateDate}
	pst_score = #{pstScore}
	pst_comment_cnt = #{pstCommentCnt}
	pst_image = #{pstImage}
	ctg_id = #{ctgId}
	mb_id = #{mbId}
WHERE = #{pstId}
